+#include "friends.hpp"
+#include "utils.h"
+#include <fstream>
+#include <sstream>
+#include <algorithm>
+
+namespace Friends {
+    std::string getFriendsFilePath(const std::string& username) {
+        return "data/friends_" + username + ".json";
+    }
+    
+    std::vector<Friend> loadFriends(const std::string& username) {
+        std::vector<Friend> friends;
+        std::string filepath = getFriendsFilePath(username);
+        std::string content = Utils::readFile(filepath);
+        
+        if (content.empty() || content == "[]") {
+            return friends;
+        }
+        
+        // Parse JSON array manually
+        size_t pos = 0;
+        while ((pos = content.find("{\"user\":", pos)) != std::string::npos) {
+            Friend f;
+            
+            // Extract user
+            size_t userStart = content.find("\"user\":\"", pos);
+            if (userStart == std::string::npos) break;
+            userStart += 8; // length of "user":"
+            size_t userEnd = content.find("\"", userStart);
+            f.user = content.substr(userStart, userEnd - userStart);
+            
+            // Extract status
+            size_t statusStart = content.find("\"status\":\"", pos);
+            if (statusStart != std::string::npos && statusStart < content.find("}", pos)) {
+                statusStart += 10; // length of "status":"
+                size_t statusEnd = content.find("\"", statusStart);
+                f.status = content.substr(statusStart, statusEnd - statusStart);
+            } else {
+                f.status = "offline";
+            }
+            
+            friends.push_back(f);
+            pos = userEnd;
+        }
+        
+        return friends;
+    }
+    
+    void saveFriends(const std::string& username, const std::vector<Friend>& friends) {
+        std::stringstream ss;
+        ss << "[";
+        for (size_t i = 0; i < friends.size(); ++i) {
+            if (i > 0) ss << ",";
+            ss << "{\"user\":\"" << friends[i].user << "\",\"status\":\"" << friends[i].status << "\"}";
+        }
+        ss << "]";
+        
+        std::string filepath = getFriendsFilePath(username);
+        Utils::writeFile(filepath, ss.str());
+    }
+    
+    bool addFriend(const std::string& username, const std::string& friendName) {
+        std::vector<Friend> friends = loadFriends(username);
+        
+        // Check if friend already exists
+        for (const auto& f : friends) {
+            if (f.user == friendName) {
+                return false;
+            }
+        }
+        
+        Friend newFriend;
+        newFriend.user = friendName;
+        newFriend.status = "offline";
+        friends.push_back(newFriend);
+        
+        saveFriends(username, friends);
+        return true;
+    }
+    
+    bool removeFriend(const std::string& username, const std::string& friendName) {
+        std::vector<Friend> friends = loadFriends(username);
+        
+        auto it = std::remove_if(friends.begin(), friends.end(),
+            [&friendName](const Friend& f) { return f.user == friendName; });
+        
+        if (it == friends.end()) {
+            return false;
+        }
+        
+        friends.erase(it, friends.end());
+        saveFriends(username, friends);
+        return true;
+    }
+    
+    std::vector<Friend> listFriends(const std::string& username) {
+        return loadFriends(username);
+    }
+    
+    Friend getFriendByIndex(const std::string& username, int index) {
+        std::vector<Friend> friends = loadFriends(username);
+        if (index >= 0 && index < static_cast<int>(friends.size())) {
+            return friends[index];
+        }
+        return {"", ""};
+    }
+}

