+#!/bin/bash
+
+echo "=== TASK 2 RUBRIC VERIFICATION ==="
+echo ""
+
+# Score_B2: Build Success
+echo "1. Build Success (Score_B2):"
+make clean > /dev/null 2>&1
+make task2 > /dev/null 2>&1
+SELFTEST_OUTPUT=$(./bin/chat_app --selftest friend-menu)
+SELFTEST_EXIT=$?
+
+if [ $SELFTEST_EXIT -eq 0 ]; then
+    echo "   ✓ make task2 compiles successfully"
+    echo "   ✓ --selftest friend-menu exits with code 0"
+    echo "   Output: $SELFTEST_OUTPUT"
+    echo "   Score_B2 = 1"
+    SCORE_B2=1
+else
+    echo "   ✗ Build or selftest failed"
+    echo "   Score_B2 = 0"
+    SCORE_B2=0
+fi
+echo ""
+
+# Score_F: Friend Persistence
+echo "2. Friend Persistence (Score_F):"
+# Clean state
+echo "[]" > data/users.json
+rm -f data/friends_user_task2.json
+rm -f logs/session_state.json
+
+# Register and add friend
+printf "1\nuser_task2\nPass123!\nPass123!\n3\n" | ./bin/chat_app > /dev/null 2>&1
+printf "2\nuser_task2\nPass123!\nA\nalice\nQ\n3\n" | ./bin/chat_app > /dev/null 2>&1
+
+# Check if friends file exists and contains alice
+if [ -f "data/friends_user_task2.json" ]; then
+    echo "   ✓ data/friends_user_task2.json exists"
+    
+    if grep -q '"user":"alice"' data/friends_user_task2.json; then
+        echo "   ✓ Contains friend alice"
+        
+        # Restart and login
+        LOGIN_OUTPUT=$(printf "2\nuser_task2\nPass123!\nL\nQ\n3\n" | ./bin/chat_app 2>&1)
+        
+        if echo "$LOGIN_OUTPUT" | grep -q "alice"; then
+            echo "   ✓ Friend persists after restart"
+            echo "   Score_F = 1"
+            SCORE_F=1
+        else
+            echo "   ✗ Friend not found after restart"
+            echo "   Score_F = 0"
+            SCORE_F=0
+        fi
+    else
+        echo "   ✗ alice not found in friends file"
+        echo "   Score_F = 0"
+        SCORE_F=0
+    fi
+else
+    echo "   ✗ friends file not created"
+    echo "   Score_F = 0"
+    SCORE_F=0
+fi
+echo ""
+
+# Score_S: Status Rendering
+echo "3. Status Rendering (Score_S):"
+LIST_OUTPUT=$(printf "2\nuser_task2\nPass123!\nL\nQ\n3\n" | ./bin/chat_app 2>&1)
+
+if echo "$LIST_OUTPUT" | grep -E '\s+-\s+\w+\s+\[(online|offline)\]'; then
+    echo "   ✓ List output matches regex: \s+-\s+\w+\s+\[(online|offline)\]"
+    echo "   Score_S = 1"
+    SCORE_S=1
+else
+    echo "   ✗ List output does not match expected format"
+    echo "   Score_S = 0"
+    SCORE_S=0
+fi
+echo ""
+
+# Score_C: Chat Entry Hook
+echo "4. Chat Entry Hook (Score_C):"
+CHAT_OUTPUT=$(printf "2\nuser_task2\nPass123!\nC\n0\n/back\nQ\n3\n" | ./bin/chat_app 2>&1)
+
+if echo "$CHAT_OUTPUT" | grep -q "--- Chat with"; then
+    echo "   ✓ Chat header displays: '--- Chat with ... ---'"
+    echo "   Score_C = 1"
+    SCORE_C=1
+else
+    echo "   ✗ Chat header not found"
+    echo "   Score_C = 0"
+    SCORE_C=0
+fi
+echo ""
+
+# Calculate final grade
+echo "=== FINAL GRADE ==="
+echo "Score_B2 = $SCORE_B2 (weight 0.25)"
+echo "Score_F  = $SCORE_F (weight 0.35)"
+echo "Score_S  = $SCORE_S (weight 0.20)"
+echo "Score_C  = $SCORE_C (weight 0.20)"
+echo ""
+
+GRADE=$(echo "scale=2; 10 * (0.25*$SCORE_B2 + 0.35*$SCORE_F + 0.20*$SCORE_S + 0.20*$SCORE_C)" | bc)
+GRADE_ROUNDED=$(printf "%.0f" $GRADE)
+
+echo "Grade = round(10 * (0.25*$SCORE_B2 + 0.35*$SCORE_F + 0.20*$SCORE_S + 0.20*$SCORE_C)) = $GRADE_ROUNDED"

