 * 2. Logout - Return to Welcome Menu
 * 3. Exit - Terminate the application
 * 
 * PERSISTENCE:
 * ------------
 * User credentials are stored in data/users.txt
 * Format: username:password (one per line)
 * File is created automatically if missing.
 * Credentials persist across application restarts.
 */
public class Main {
    private static UserManager userManager;
    private static Scanner scanner;
    private static String currentUser;
    private static boolean running;

    public static void main(String[] args) {
        userManager = new UserManager();
        scanner = new Scanner(System.in);
        running = true;

        System.out.println("=================================");
        System.out.println("  Task Management System");
        System.out.println("=================================");

        while (running) {
            showWelcomeMenu();
        }

        System.out.println("Goodbye!");
        scanner.close();
    }

    /**
     * Display the welcome menu and handle user selection.
     */
    private static void showWelcomeMenu() {
        System.out.println("\n--- Welcome Menu ---");
        System.out.println("1. Register");
        System.out.println("2. Login");
        System.out.println("3. Exit");
        System.out.print("Please select an option (1-3): ");

        String choice = readLine();
        if (choice == null) {
            running = false;
            return;
        }

        switch (choice.trim()) {
            case "1":
                handleRegistration();
                break;
            case "2":
                handleLogin();
                break;
            case "3":
                running = false;
                break;
            default:
                System.out.println("Invalid option. Please try again.");
        }
    }

    /**
     * Handle user registration flow.
     */
    private static void handleRegistration() {
        System.out.println("\n--- Registration ---");

        String username;
        while (true) {
            System.out.print("Enter username: ");
            username = readLine();
            if (username == null) {
                running = false;
                return;
            }
            username = username.trim();

            if (username.isEmpty()) {
                System.out.println("Username cannot be empty. Please try again.");
                continue;
            }

            if (userManager.userExists(username)) {
                System.out.println("Username already exists. Please choose a different username.");
                continue;
            }

            break;
        }

        String password;
        while (true) {
            System.out.print("Enter password: ");
            password = readLine();
            if (password == null) {
                running = false;
                return;
            }

            if (password.isEmpty()) {
                System.out.println("Password cannot be empty. Please try again.");
                continue;
            }

            break;
        }

        while (true) {
            System.out.print("Confirm password: ");
            String confirmPassword = readLine();
            if (confirmPassword == null) {
                running = false;
                return;
            }

            if (!password.equals(confirmPassword)) {
                System.out.println("Passwords do not match. Please try again.");
                continue;
            }

            break;
        }

        if (userManager.registerUser(username, password)) {
            System.out.println("Registration successful! You can now login.");
        } else {
            System.out.println("Registration failed. Username may already exist.");
        }
    }

    /**
     * Handle user login flow.
     */
    private static void handleLogin() {
        System.out.println("\n--- Login ---");

        while (true) {
            System.out.print("Enter username: ");
            String username = readLine();
            if (username == null) {
                running = false;
                return;
            }
            username = username.trim();

            System.out.print("Enter password: ");
            String password = readLine();
            if (password == null) {
                running = false;
                return;
            }

            if (userManager.validateLogin(username, password)) {
                System.out.println("Login successful! Welcome, " + username + "!");
                currentUser = username;
                showMainMenu();
                return;
            } else {
                System.out.println("Invalid username or password.");
                System.out.print("Try again? (y/n): ");
                String retry = readLine();
                if (retry == null) {
                    running = false;
                    return;
                }
                if (!retry.trim().toLowerCase().startsWith("y")) {
                    return;
                }
            }
        }
    }

    /**
     * Display the main menu for authenticated users.
     */
    private static void showMainMenu() {
        while (running) {
            System.out.println("\n--- Main Menu ---");
            System.out.println("Logged in as: " + currentUser);
            System.out.println("1. View Tasks");
            System.out.println("2. Logout");
            System.out.println("3. Exit");
            System.out.print("Please select an option (1-3): ");

            String choice = readLine();
            if (choice == null) {
                running = false;
                return;
            }

            switch (choice.trim()) {
                case "1":
                    viewTasks();
                    break;
                case "2":
                    System.out.println("Logged out successfully.");
                    currentUser = null;
                    return;
                case "3":
                    running = false;
                    return;
                default:
                    System.out.println("Invalid option. Please try again.");
            }
        }
    }

    /**
     * Placeholder for viewing tasks.
     */
    private static void viewTasks() {
        System.out.println("\n--- Your Tasks ---");
        System.out.println("No tasks available. (Task management coming soon!)");
