    </head>
    <body>
      <div id="game-container">
        <div id="hud">
          <span id="timer">Time: 00:00</span>
          <span id="pairs-remaining">Pairs: 40</span>
        </div>

        <div id="puzzle-wrapper">
          <div id="column-labels">
            <span>A</span><span>B</span><span>C</span><span>D</span><span>E</span>
            <span>F</span><span>G</span><span>H</span><span>I</span><span>J</span>
          </div>
          <div id="board-area">
            <div id="row-labels">
              <span>1</span><span>2</span><span>3</span><span>4</span>
              <span>5</span><span>6</span><span>7</span><span>8</span>
            </div>
            <div id="tile-grid"></div>
          </div>
        </div>

        <div id="legend">
          <h3>Tile Colors</h3>
          <div class="legend-items">
            <div class="legend-item"><span class="color-box" style="background:#FF6B6B;"></span>Red - Hearts</div>
            <div class="legend-item"><span class="color-box" style="background:#4ECDC4;"></span>Teal - Diamonds</div>
            <div class="legend-item"><span class="color-box" style="background:#45B7D1;"></span>Blue - Clubs</div>
            <div class="legend-item"><span class="color-box" style="background:#96CEB4;"></span>Green - Spades</div>
            <div class="legend-item"><span class="color-box" style="background:#FFEAA7;"></span>Yellow - Stars</div>
            <div class="legend-item"><span class="color-box" style="background:#DDA0DD;"></span>Purple - Moons</div>
            <div class="legend-item"><span class="color-box" style="background:#F39C12;"></span>Orange - Suns</div>
            <div class="legend-item"><span class="color-box" style="background:#E056FD;"></span>Pink - Flowers</div>
          </div>
        </div>

        <button id="start-btn">Start Puzzle</button>
      </div>

      <script src="app.js"></script>
    </body>
    </html>

