    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }

    body {
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
      background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
      min-height: 100vh;
      display: flex;
      justify-content: center;
      align-items: center;
      color: #fff;
    }

    #game-container {
      display: flex;
      flex-direction: column;
      align-items: center;
      position: relative;
    }

    #hud {
      position: absolute;
      top: -48px;
      left: 50%;
      transform: translateX(-50%);
      display: flex;
      gap: 40px;
      font-size: 18px;
      font-weight: 600;
      background: rgba(255, 255, 255, 0.1);
      padding: 10px 30px;
      border-radius: 8px;
      backdrop-filter: blur(5px);
    }

    #hud span {
      color: #fff;
    }

    #timer {
      color: #4ECDC4 !important;
    }

    #pairs-remaining {
      color: #FF6B6B !important;
    }

    #puzzle-wrapper {
      display: flex;
      flex-direction: column;
      align-items: center;
    }

    #column-labels {
      display: flex;
      width: 640px;
      margin-left: 24px;
      margin-bottom: 4px;
    }

    #column-labels span {
      width: 64px;
      text-align: center;
      font-weight: bold;
      font-size: 14px;
      color: #aaa;
    }

    #board-area {
      display: flex;
    }

    #row-labels {
      display: flex;
      flex-direction: column;
      margin-right: 4px;
      justify-content: flex-start;
    }

    #row-labels span {
      height: 64px;
      width: 20px;
      display: flex;
      align-items: center;
      justify-content: center;
      font-weight: bold;
      font-size: 14px;
      color: #aaa;
    }

    #tile-grid {
      width: 640px;
      height: 512px;
      display: grid;
      grid-template-columns: repeat(10, 64px);
      grid-template-rows: repeat(8, 64px);
      background: rgba(0, 0, 0, 0.3);
      border-radius: 8px;
      border: 2px solid rgba(255, 255, 255, 0.1);
    }

    .tile {
      width: 64px;
      height: 64px;
      display: flex;
      align-items: center;
      justify-content: center;
      background: rgba(255, 255, 255, 0.08);
      border: 1px solid rgba(255, 255, 255, 0.15);
      border-radius: 6px;
      cursor: pointer;
      transition: all 0.2s ease;
      font-size: 28px;
      color: rgba(255, 255, 255, 0.3);
    }

    .tile:hover {
      background: rgba(255, 255, 255, 0.15);
      transform: scale(1.02);
    }

    .tile.selected {
      border-color: #4ECDC4;
      box-shadow: 0 0 10px rgba(78, 205, 196, 0.5);
    }

    .tile.matched {
      opacity: 0;
      pointer-events: none;
    }

    #legend {
      margin-top: 70px;
      background: rgba(255, 255, 255, 0.05);
      padding: 15px 25px;
      border-radius: 8px;
      text-align: center;
    }

    #legend h3 {
      margin-bottom: 12px;
      font-size: 16px;
      color: #ccc;
    }

    .legend-items {
      display: flex;
      flex-wrap: wrap;
      gap: 12px;
      justify-content: center;
      max-width: 640px;
    }

    .legend-item {
      display: flex;
      align-items: center;
      gap: 6px;
      font-size: 12px;
      color: #aaa;
    }

    .color-box {
      width: 16px;
      height: 16px;
      border-radius: 3px;
      border: 1px solid rgba(255, 255, 255, 0.2);
    }

    #start-btn {
      position: absolute;
      top: 110px;
      left: 120px;
      transform: translate(-50%, -50%);
      padding: 15px 35px;
      font-size: 20px;
      font-weight: bold;
      background: linear-gradient(135deg, #4ECDC4 0%, #44A08D 100%);
      color: #fff;
      border: none;
      border-radius: 30px;
      cursor: pointer;
      box-shadow: 0 4px 15px rgba(78, 205, 196, 0.4);
      transition: all 0.3s ease;
      z-index: 10;
    }

    #start-btn:hover {
      transform: translate(-50%, -50%) scale(1.05);
      box-shadow: 0 6px 20px rgba(78, 205, 196, 0.6);
    }

    #start-btn:active {
      transform: translate(-50%, -50%) scale(0.98);
    }

