  const COLUMNS = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J'];
  const ROWS = [1, 2, 3, 4, 5, 6, 7, 8];
  const GLYPHS = ['♥', '♦', '♣', '♠', '★', '☽', '☀', '✿'];

  let gridInitialized = false;

  function initializeGrid() {
    if (gridInitialized) {
      return;
    }

    const tileGrid = document.getElementById('tile-grid');
    if (!tileGrid) {
      console.error('tile-grid element not found');
      return;
    }

    tileGrid.innerHTML = '';

    const values = [];
    for (let i = 0; i < 40; i++) {
      const value = i % 8;
      values.push(value, value);
    }

    for (let i = values.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [values[i], values[j]] = [values[j], values[i]];
    }

    let index = 0;
    for (let row = 0; row < ROWS.length; row++) {
      for (let col = 0; col < COLUMNS.length; col++) {
        const tile = document.createElement('div');
        tile.className = 'tile';
        const cellId = COLUMNS[col] + ROWS[row];
        tile.setAttribute('data-cell', cellId);
        const value = values[index];
        tile.setAttribute('data-value', value.toString());
        tile.textContent = GLYPHS[value];
        tile.setAttribute('aria-label', 'Tile ' + cellId + ' value ' + GLYPHS[value]);
        tileGrid.appendChild(tile);
        index++;
      }
    }

    updateHUD();
    gridInitialized = true;
  }

  function updateHUD() {
    const timerEl = document.getElementById('timer');
    const pairsEl = document.getElementById('pairs-remaining');

    if (timerEl) {
      timerEl.textContent = 'Time: 00:00';
    }
    if (pairsEl) {
      pairsEl.textContent = 'Pairs: 40';
    }
  }

  function describeLayout() {
    const result = {
      startButton: null,
      legend: null,
      hud: null,
      tiles: {}
    };

    const startBtn = document.getElementById('start-btn');
    if (startBtn) {
      const rect = startBtn.getBoundingClientRect();
      result.startButton = {
        x: Math.round(rect.left + rect.width / 2),
        y: Math.round(rect.top + rect.height / 2)
      };
    }

    const legend = document.getElementById('legend');
    if (legend) {
      const rect = legend.getBoundingClientRect();
      result.legend = {
        top: Math.round(rect.top),
        left: Math.round(rect.left),
        width: Math.round(rect.width),
        height: Math.round(rect.height)
      };
    }

    const hud = document.getElementById('hud');
    if (hud) {
      const rect = hud.getBoundingClientRect();
      result.hud = {
        top: Math.round(rect.top),
        left: Math.round(rect.left),
        width: Math.round(rect.width),
        height: Math.round(rect.height)
      };
    }

    const tiles = document.querySelectorAll('.tile');
    tiles.forEach(function(tile) {
      const cellId = tile.getAttribute('data-cell');
      if (cellId) {
        const rect = tile.getBoundingClientRect();
        result.tiles[cellId] = {
          x: Math.round(rect.left + rect.width / 2),
          y: Math.round(rect.top + rect.height / 2)
        };
      }
    });

    return result;
  }

  window.app = {
    initializeGrid: initializeGrid,
    describeLayout: describeLayout
  };

  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', function() {
      window.app.initializeGrid();
    });
  } else {
    window.app.initializeGrid();
  }
})();"
    }
