            <div class="legend-item"><span class="color-box" style="background:#E056FD;"></span>Pink</div>
          </div>
        </div>
      </div>

      <script src="app.js"></script>
    </body>
    </html>

[[workspace]] styles.css
    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }

    body {
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
      background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
      min-height: 100vh;
      color: #fff;
      position: relative;
    }

    #start-btn {
      position: absolute;
      top: 110px;
      left: 120px;
      transform: translate(-50%, -50%);
      padding: 12px 24px;
      font-size: 16px;
      font-weight: bold;
      background: linear-gradient(135deg, #4ECDC4 0%, #44A08D 100%);
      color: #fff;
      border: none;
      border-radius: 20px;
      cursor: pointer;
      box-shadow: 0 4px 15px rgba(78, 205, 196, 0.4);
      z-index: 100;
    }

    #start-btn:hover {
      transform: translate(-50%, -50%) scale(1.05);
    }

    #game-container {
      display: flex;
      flex-direction: column;
      align-items: center;
      padding-top: 48px;
    }

    #hud {
      display: flex;
      gap: 40px;
      font-size: 18px;
      font-weight: 600;
      background: rgba(255, 255, 255, 0.1);
      padding: 10px 30px;
      border-radius: 8px;
      margin-bottom: 10px;
    }

    #timer {
      color: #4ECDC4;
    }

    #pairs-remaining {
      color: #FF6B6B;
    }

    #puzzle-wrapper {
      display: flex;
      flex-direction: column;
      align-items: center;
    }

    #column-labels {
      display: flex;
      width: 640px;
      margin-left: 24px;
      margin-bottom: 4px;
    }

    #column-labels span {
      width: 64px;
      text-align: center;
      font-weight: bold;
      font-size: 14px;
      color: #aaa;
    }

    #board-area {
      display: flex;
    }

    #row-labels {
      display: flex;
      flex-direction: column;
      margin-right: 4px;
    }

    #row-labels span {
      height: 64px;
      width: 20px;
      display: flex;
      align-items: center;
      justify-content: center;
      font-weight: bold;
      font-size: 14px;
      color: #aaa;
    }

    #tile-grid {
      width: 640px;
      height: 512px;
      display: grid;
      grid-template-columns: repeat(10, 64px);
      grid-template-rows: repeat(8, 64px);
      background: rgba(0, 0, 0, 0.3);
      border-radius: 8px;
      border: 2px solid rgba(255, 255, 255, 0.1);
    }

    .tile {
      width: 64px;
      height: 64px;
      display: flex;
      align-items: center;
      justify-content: center;
      background: rgba(255, 255, 255, 0.08);
      border: 1px solid rgba(255, 255, 255, 0.15);
      border-radius: 6px;
      cursor: pointer;
      transition: all 0.2s ease;
      font-size: 28px;
      color: rgba(255, 255, 255, 0.3);
    }

    .tile:hover {
      background: rgba(255, 255, 255, 0.15);
    }

    #legend {
      margin-top: 70px;
      background: rgba(255, 255, 255, 0.05);
      padding: 15px 25px;
      border-radius: 8px;
      text-align: center;
    }

    #legend h3 {
      margin-bottom: 10px;
      font-size: 14px;
      color: #ccc;
    }

    .legend-items {
      display: flex;
      flex-wrap: wrap;
      gap: 15px;
      justify-content: center;
    }

    .legend-item {
      display: flex;
      align-items: center;
      gap: 5px;
      font-size: 12px;
      color: #aaa;
    }

    .color-box {
      width: 14px;
      height: 14px;
      border-radius: 3px;
    }

