    for (let i = activeTiles.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [activeTiles[i], activeTiles[j]] = [activeTiles[j], activeTiles[i]];
    }

    activeCoords.forEach(function(coord, idx) {
      const tile = document.querySelector('[data-cell="' + coord + '"]');
      if (tile) {
        const value = activeTiles[idx];
        tile.setAttribute('data-value', value);
        tileValues[coord] = value;
        if (typeof value === 'number' || !isNaN(parseInt(value))) {
