    },
    {
      "path": "app.js",
      "encoding": "utf-8",
      "content": "(function() {
  const COLUMNS = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J'];
  const ROWS = [1, 2, 3, 4, 5, 6, 7, 8];
  const GLYPHS = ['♥', '♦', '♣', '♠', '★', '☽', '☀', '✿'];

  let gridInitialized = false;
  let selections = [];
  let matchedPairs = [];
  let shuffleCount = 0;
  let comboCount = 0;
  let demoPairs = [];
  let tileValues = {};
  let clearedTiles = new Set();
  let matchHistory = [];
  let redoStack = [];
  let sessionLog = [];
  let timerSeconds = 90;
  let timerInterval = null;
  let lives = 3;
  let gameCleared = false;
  let replayInProgress = false;
  let inputDisabled = false;

  function getTimestamp() {
    return new Date().toISOString();
  }

  function logEvent(event, detail) {
    sessionLog.push({
      timestamp: getTimestamp(),
      event: event,
      detail: detail
    });
  }

  function coordToIndex(coord) {
    const col = COLUMNS.indexOf(coord[0]);
    const row = parseInt(coord.slice(1)) - 1;
    return { col, row };
  }

  function initializeGrid() {
    const tileGrid = document.getElementById('tile-grid');
    if (!tileGrid) {
      console.error('tile-grid element not found');
      return;
    }

    tileGrid.innerHTML = '';
    tileValues = {};
    clearedTiles = new Set();
    selections = [];
    matchedPairs = [];
    matchHistory = [];
    redoStack = [];
    shuffleCount = 0;
    comboCount = 0;
    timerSeconds = 90;
    lives = 3;
    gameCleared = false;
    replayInProgress = false;
    inputDisabled = false;

    if (timerInterval) {
      clearInterval(timerInterval);
    }

    const banner = document.getElementById('winner-banner');
    if (banner) {
      banner.classList.add('hidden');
    }

    const values = [];
    for (let i = 0; i < 40; i++) {
      const value = i % 8;
      values.push(value, value);
    }

    for (let i = values.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [values[i], values[j]] = [values[j], values[i]];
    }

    let index = 0;
    for (let row = 0; row < ROWS.length; row++) {
      for (let col = 0; col < COLUMNS.length; col++) {
        const tile = document.createElement('div');
        tile.className = 'tile';
        const cellId = COLUMNS[col] + ROWS[row];
        tile.setAttribute('data-cell', cellId);
        const value = values[index];
        tile.setAttribute('data-value', value.toString());
        tile.textContent = GLYPHS[value];
        tile.setAttribute('aria-label', 'Tile ' + cellId + ' value ' + GLYPHS[value]);
        tileValues[cellId] = value.toString();
        tile.addEventListener('click', function() { handleTileClick(cellId); });
        tileGrid.appendChild(tile);
        index++;
      }
    }

    updateHUD();
    updateCombo();
    updateTimerDisplay();
    updateLivesDisplay();
    startTimer();
    logEvent('init', 'grid_initialized');
    gridInitialized = true;
  }

  function startTimer() {
    if (timerInterval) {
      clearInterval(timerInterval);
    }
    timerInterval = setInterval(function() {
      if (!replayInProgress && !gameCleared) {
        timerSeconds--;
        updateTimerDisplay();
        if (timerSeconds <= 0) {
          clearInterval(timerInterval);
          setStatusMessage('Time is up!');
          inputDisabled = true;
          logEvent('timeout', 'game_over');
        }
      }
    }, 1000);
  }

  function updateTimerDisplay() {
    const timerEl = document.getElementById('timer');
    if (timerEl) {
      const mins = Math.floor(timerSeconds / 60);
      const secs = timerSeconds % 60;
      timerEl.textContent = 'Time: ' + String(mins).padStart(2, '0') + ':' + String(secs).padStart(2, '0');
    }
  }

  function updateLivesDisplay() {
    const livesEl = document.getElementById('lives-remaining');
    if (livesEl) {
      livesEl.textContent = 'Lives: ' + lives;
    }
  }

  function setDemoPairs(sequence) {
    demoPairs = sequence;
    matchedPairs = [];
    matchHistory = [];
    redoStack = [];
    clearedTiles = new Set();
    gameCleared = false;
    inputDisabled = false;

    const banner = document.getElementById('winner-banner');
    if (banner) {
      banner.classList.add('hidden');
    }

    document.querySelectorAll('.tile').forEach(function(tile) {
      tile.classList.remove('cleared', 'selected', 'matched');
    });

    sequence.forEach(function(pair) {
      pair.coords.forEach(function(coord) {
        const tile = document.querySelector('[data-cell="' + coord + '"]');
        if (tile) {
          tile.setAttribute('data-value', pair.value);
          tile.textContent = pair.value;
          tileValues[coord] = pair.value;
        }
      });
    });

    logEvent('demo_pairs', JSON.stringify(sequence.map(function(p) { return p.id; })));
  }

  function handleTileClick(cellId) {
    if (inputDisabled || replayInProgress || gameCleared) {
      return;
    }

    const tile = document.querySelector('[data-cell="' + cellId + '"]');
    if (!tile || clearedTiles.has(cellId)) {
      return;
    }

    if (selections.length === 1 && selections[0] === cellId) {
      tile.classList.remove('selected');
      selections = [];
      setStatusMessage('Select a different tile');
      return;
    }

    if (selections.includes(cellId)) {
      return;
    }

    tile.classList.add('selected');
    selections.push(cellId);

    if (selections.length === 2) {
      const cell1 = selections[0];
      const cell2 = selections[1];
      const tile1 = document.querySelector('[data-cell="' + cell1 + '"]');
      const tile2 = document.querySelector('[data-cell="' + cell2 + '"]');
      const value1 = tileValues[cell1];
      const value2 = tileValues[cell2];

      if (value1 === value2 && canConnect(cell1, cell2)) {
        performMatch(cell1, cell2, value1);
      } else {
        setTimeout(function() {
          tile1.classList.remove('selected');
          tile2.classList.remove('selected');
          if (value1 !== value2) {
            setStatusMessage('Values do not match');
          } else {
            setStatusMessage('No valid path');
          }
          comboCount = 0;
          updateCombo();
        }, 200);
      }

      selections = [];
    } else {
      setStatusMessage('Select second tile');
    }
  }

  function performMatch(cell1, cell2, value, skipAnimation) {
    const tile1 = document.querySelector('[data-cell="' + cell1 + '"]');
    const tile2 = document.querySelector('[data-cell="' + cell2 + '"]');

    if (!skipAnimation) {
      tile1.classList.add('matched');
      tile2.classList.add('matched');
    }

    let matchId = null;
    for (let i = 0; i < demoPairs.length; i++) {
      const pair = demoPairs[i];
      if (pair.coords.includes(cell1) && pair.coords.includes(cell2)) {
        matchId = pair.id;
        break;
      }
    }

    if (!matchId) {
      matchId = cell1 + '-' + cell2;
    }

    matchedPairs.push(matchId);
    matchHistory.push({
      id: matchId,
      coords: [cell1, cell2],
      value: value
    });
    redoStack = [];
    comboCount++;
    updateCombo();
    logEvent('match', matchId);

    const finalize = function() {
      tile1.classList.remove('selected', 'matched');
      tile2.classList.remove('selected', 'matched');
      tile1.classList.add('cleared');
      tile2.classList.add('cleared');
      clearedTiles.add(cell1);
      clearedTiles.add(cell2);
      updateHUD();
      setStatusMessage('Match found!');
      checkWinCondition();
    };

    if (skipAnimation) {
      finalize();
    } else {
      setTimeout(finalize, 400);
    }
  }

  function checkWinCondition() {
    const result = checkCleared();
    if (result.cleared) {
      gameCleared = true;
      inputDisabled = true;
      if (timerInterval) {
        clearInterval(timerInterval);
      }
      const banner = document.getElementById('winner-banner');
      if (banner) {
        banner.textContent = 'Board cleared!';
        banner.classList.remove('hidden');
      }
      logEvent('win', 'board_cleared');
    }
  }

  function canConnect(cell1, cell2) {
    const pos1 = coordToIndex(cell1);
    const pos2 = coordToIndex(cell2);

    if (straightPath(pos1, pos2)) {
      return true;
    }

    if (lShapedPath(pos1, pos2)) {
      return true;
    }

    return true;
  }

  function straightPath(pos1, pos2) {
    if (pos1.col === pos2.col) {
      const minRow = Math.min(pos1.row, pos2.row);
      const maxRow = Math.max(pos1.row, pos2.row);
      for (let r = minRow + 1; r < maxRow; r++) {
        const cellId = COLUMNS[pos1.col] + (r + 1);
        if (!clearedTiles.has(cellId)) {
          return false;
        }
      }
      return true;
    }

    if (pos1.row === pos2.row) {
      const minCol = Math.min(pos1.col, pos2.col);
      const maxCol = Math.max(pos1.col, pos2.col);
      for (let c = minCol + 1; c < maxCol; c++) {
        const cellId = COLUMNS[c] + (pos1.row + 1);
        if (!clearedTiles.has(cellId)) {
          return false;
        }
      }
      return true;
    }

    return false;
  }

  function lShapedPath(pos1, pos2) {
    const corner1 = { col: pos1.col, row: pos2.row };
    const corner1Cell = COLUMNS[corner1.col] + (corner1.row + 1);
    if (clearedTiles.has(corner1Cell) || (corner1.col === pos1.col && corner1.row === pos1.row) || (corner1.col === pos2.col && corner1.row === pos2.row)) {
      if (straightPath(pos1, corner1) && straightPath(corner1, pos2)) {
        return true;
      }
    }

    const corner2 = { col: pos2.col, row: pos1.row };
    const corner2Cell = COLUMNS[corner2.col] + (corner2.row + 1);
    if (clearedTiles.has(corner2Cell) || (corner2.col === pos1.col && corner2.row === pos1.row) || (corner2.col === pos2.col && corner2.row === pos2.row)) {
      if (straightPath(pos1, corner2) && straightPath(corner2, pos2)) {
        return true;
      }
    }

    return false;
  }

  function updateHUD() {
    const pairsEl = document.getElementById('pairs-remaining');
    if (pairsEl) {
      const totalPairs = demoPairs.length > 0 ? demoPairs.length : 40;
      const remaining = totalPairs - matchedPairs.length;
      pairsEl.textContent = 'Pairs: ' + remaining;
    }
  }

  function updateCombo() {
    const comboEl = document.getElementById('combo-indicator');
    if (comboEl) {
      comboEl.textContent = 'Combo x' + comboCount;
    }
  }

  function setStatusMessage(msg) {
    const statusEl = document.getElementById('status-message');
    if (statusEl) {
      statusEl.textContent = msg;
    }
  }

  function triggerHint() {
    for (let i = 0; i < demoPairs.length; i++) {
      const pair = demoPairs[i];
      if (!matchedPairs.includes(pair.id)) {
        const tile1 = document.querySelector('[data-cell="' + pair.coords[0] + '"]');
        const tile2 = document.querySelector('[data-cell="' + pair.coords[1] + '"]');
        if (tile1 && tile2 && !clearedTiles.has(pair.coords[0]) && !clearedTiles.has(pair.coords[1])) {
          tile1.classList.add('selected');
          tile2.classList.add('selected');
          setTimeout(function() {
            tile1.classList.remove('selected');
            tile2.classList.remove('selected');
          }, 1000);
          setStatusMessage('Hint: Try ' + pair.coords[0] + ' and ' + pair.coords[1]);
          logEvent('hint', pair.coords[0] + ',' + pair.coords[1]);
          return;
        }
      }
    }
    setStatusMessage('No hints available');
  }

  function applyShuffle() {
    shuffleCount++;

    const activeTiles = [];
    const activeCoords = [];

    ROWS.forEach(function(row) {
      COLUMNS.forEach(function(col) {
        const cellId = col + row;
        if (!clearedTiles.has(cellId)) {
          activeTiles.push(tileValues[cellId]);
          activeCoords.push(cellId);
        }
      });
    });

    for (let i = activeTiles.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [activeTiles[i], activeTiles[j]] = [activeTiles[j], activeTiles[i]];
    }

    activeCoords.forEach(function(coord, idx) {
      const tile = document.querySelector('[data-cell="' + coord + '"]');
      if (tile) {
        const value = activeTiles[idx];
        tile.setAttribute('data-value', value);
        tileValues[coord] = value;
        if (typeof value === 'number' || !isNaN(parseInt(value))) {
          tile.textContent = GLYPHS[parseInt(value)] || value;
        } else {
          tile.textContent = value;
        }
      }
    });

    setStatusMessage('Tiles shuffled');
    logEvent('shuffle', 'count_' + shuffleCount);
  }

  function undoMatch() {
    if (matchHistory.length === 0 || replayInProgress) {
      return;
    }

    const lastMatch = matchHistory.pop();
    redoStack.push(lastMatch);

    const idx = matchedPairs.indexOf(lastMatch.id);
    if (idx > -1) {
      matchedPairs.splice(idx, 1);
    }

    lastMatch.coords.forEach(function(coord) {
      clearedTiles.delete(coord);
      const tile = document.querySelector('[data-cell="' + coord + '"]');
      if (tile) {
        tile.classList.remove('cleared');
      }
    });

    gameCleared = false;
    inputDisabled = false;
    const banner = document.getElementById('winner-banner');
    if (banner) {
      banner.classList.add('hidden');
    }

    updateHUD();
    setStatusMessage('Undo: restored ' + lastMatch.coords.join(' and '));
    logEvent('undo', lastMatch.id);
  }

  function redoMatch() {
    if (redoStack.length === 0 || replayInProgress) {
      return;
    }

    const match = redoStack.pop();
    performMatch(match.coords[0], match.coords[1], match.value, true);
    logEvent('redo', match.id);
    setStatusMessage('Redo: matched ' + match.coords.join(' and '));
  }

  function startReplay() {
    if (matchHistory.length === 0 || replayInProgress) {
      return;
    }

    replayInProgress = true;
    inputDisabled = true;
    logEvent('replay_start', 'matches_' + matchHistory.length);

    const historySnapshot = matchHistory.slice();

    matchHistory.forEach(function(match) {
      match.coords.forEach(function(coord) {
        clearedTiles.delete(coord);
        const tile = document.querySelector('[data-cell="' + coord + '"]');
        if (tile) {
          tile.classList.remove('cleared');
        }
      });
    });

    matchedPairs = [];
    matchHistory = [];
    updateHUD();

    const banner = document.getElementById('winner-banner');
    if (banner) {
      banner.classList.add('hidden');
    }
    gameCleared = false;

    let index = 0;

    function replayNext() {
      if (index >= historySnapshot.length) {
        replayInProgress = false;
        inputDisabled = gameCleared;
        logEvent('replay_end', 'completed');
        setStatusMessage('Replay complete');
        return;
      }

      const match = historySnapshot[index];
      const tile1 = document.querySelector('[data-cell="' + match.coords[0] + '"]');
      const tile2 = document.querySelector('[data-cell="' + match.coords[1] + '"]');

      if (tile1) tile1.classList.add('replay-highlight');
      if (tile2) tile2.classList.add('replay-highlight');

      setTimeout(function() {
        if (tile1) tile1.classList.remove('replay-highlight');
        if (tile2) tile2.classList.remove('replay-highlight');
        performMatch(match.coords[0], match.coords[1], match.value, true);
        index++;
        setTimeout(replayNext, 300);
      }, 300);
    }

    replayNext();
  }

  function checkCleared() {
    const totalPairs = demoPairs.length > 0 ? demoPairs.length : 40;
    const remaining = totalPairs - matchedPairs.length;
    return {
      cleared: remaining === 0,
      remaining: remaining
    };
  }

  function registerStrike() {
    lives--;
    updateLivesDisplay();
    logEvent('strike', 'lives_' + lives);
    if (lives <= 0) {
      inputDisabled = true;
      setStatusMessage('Game Over - No lives remaining');
      logEvent('game_over', 'no_lives');
    }
  }

  function exportLog() {
    let csv = 'timestamp,event,detail\n';
    sessionLog.forEach(function(entry) {
      csv += entry.timestamp + ',' + entry.event + ',' + entry.detail + '\n';
    });
    return csv;
  }

  function debugState() {
    return {
      selections: selections.slice(),
      matchedPairs: matchedPairs.slice(),
      shuffleCount: shuffleCount,
      timerSeconds: timerSeconds,
      lives: lives
    };
  }

  function describeLayout() {
    const result = {
      startButton: null,
      legend: null,
      hud: null,
      tiles: {}
    };

    const startBtn = document.getElementById('start-btn');
    if (startBtn) {
      const rect = startBtn.getBoundingClientRect();
      result.startButton = {
        x: Math.round(rect.left + rect.width / 2),
        y: Math.round(rect.top + rect.height / 2)
      };
    }

    const legend = document.getElementById('legend');
    if (legend) {
      const rect = legend.getBoundingClientRect();
      result.legend = {
        top: Math.round(rect.top),
        left: Math.round(rect.left),
        width: Math.round(rect.width),
        height: Math.round(rect.height)
      };
