      align-items: center;
      justify-content: center;
      font-weight: bold;
      font-size: 14px;
      color: #aaa;
    }

    #tile-grid {
      width: 640px;
      height: 512px;
      display: grid;
      grid-template-columns: repeat(10, 64px);
      grid-template-rows: repeat(8, 64px);
