      background: rgba(255, 255, 255, 0.15);
      color: #fff;
      border: 1px solid rgba(255, 255, 255, 0.25);
      border-radius: 6px;
      cursor: pointer;
      transition: all 0.2s ease;
    }

    #controls button:hover {
      background: rgba(255, 255, 255, 0.25);
    }

    #controls button:disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    #legend {
      margin-top: 70px;
      background: rgba(255, 255, 255, 0.05);
      padding: 15px 25px;
      border-radius: 8px;
      text-align: center;
    }

    #legend h3 {
      margin-bottom: 10px;
      font-size: 14px;
      color: #ccc;
    }

    .legend-items {
      display: flex;
      flex-wrap: wrap;
      gap: 15px;
      justify-content: center;
    }

    .legend-item {
      display: flex;
      align-items: center;
      gap: 5px;
      font-size: 12px;
      color: #aaa;
    }

    .color-box {
      width: 14px;
      height: 14px;
      border-radius: 3px;
    }

