      "path": "app.js",
      "encoding": "utf-8",
      "content": "(function() {
  var COLUMNS = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J'];
  var ROWS = [1, 2, 3, 4, 5, 6, 7, 8];
  var GLYPHS = ['♥', '♦', '♣', '♠', '★', '☽', '☀', '✿'];

  var gridInitialized = false;
  var selections = [];
  var matchedPairs = [];
  var shuffleCount = 0;
  var comboCount = 0;
  var demoPairs = [];
  var tileValues = {};
  var clearedTiles = {};
  var matchHistory = [];
  var redoStack = [];
  var sessionLog = [];
  var timerSeconds = 90;
  var timerInterval = null;
  var lives = 3;
  var gameCleared = false;
  var replayInProgress = false;
  var inputDisabled = false;

  function getTimestamp() {
    return new Date().toISOString();
  }

  function logEvent(event, detail) {
    sessionLog.push({
      timestamp: getTimestamp(),
      event: event,
      detail: detail
    });
  }

  function coordToIndex(coord) {
    var col = COLUMNS.indexOf(coord[0]);
    var row = parseInt(coord.slice(1)) - 1;
    return { col: col, row: row };
  }

  function initializeGrid() {
    var tileGrid = document.getElementById('tile-grid');
    if (!tileGrid) return;

    tileGrid.innerHTML = '';
    tileValues = {};
    clearedTiles = {};
    selections = [];
    matchedPairs = [];
    matchHistory = [];
    redoStack = [];
    shuffleCount = 0;
    comboCount = 0;
    timerSeconds = 90;
    lives = 3;
    gameCleared = false;
    replayInProgress = false;
    inputDisabled = false;

    if (timerInterval) clearInterval(timerInterval);

    var banner = document.getElementById('winner-banner');
    if (banner) banner.classList.add('hidden');

    var values = [];
    for (var i = 0; i < 40; i++) {
      var value = i % 8;
      values.push(value, value);
    }

    for (var i = values.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = values[i];
      values[i] = values[j];
      values[j] = temp;
    }

    var index = 0;
    for (var row = 0; row < ROWS.length; row++) {
      for (var col = 0; col < COLUMNS.length; col++) {
        var tile = document.createElement('div');
        tile.className = 'tile';
        var cellId = COLUMNS[col] + ROWS[row];
        tile.setAttribute('data-cell', cellId);
        var value = values[index];
        tile.setAttribute('data-value', value.toString());
        tile.textContent = GLYPHS[value];
        tile.setAttribute('aria-label', 'Tile ' + cellId + ' value ' + GLYPHS[value]);
        tileValues[cellId] = value.toString();
        (function(id) {
          tile.addEventListener('click', function() { handleTileClick(id); });
        })(cellId);
        tileGrid.appendChild(tile);
        index++;
      }
    }

    updateHUD();
    updateCombo();
    updateTimerDisplay();
    updateLivesDisplay();
    startTimer();
    logEvent('init', 'grid_initialized');
    gridInitialized = true;
  }

  function startTimer() {
    if (timerInterval) clearInterval(timerInterval);
    timerInterval = setInterval(function() {
      if (!replayInProgress && !gameCleared) {
        timerSeconds--;
        updateTimerDisplay();
        if (timerSeconds <= 0) {
          clearInterval(timerInterval);
          setStatusMessage('Time is up!');
          inputDisabled = true;
          logEvent('timeout', 'game_over');
        }
      }
    }, 1000);
  }

  function updateTimerDisplay() {
    var timerEl = document.getElementById('timer');
    if (timerEl) {
      var mins = Math.floor(timerSeconds / 60);
      var secs = timerSeconds % 60;
      timerEl.textContent = 'Time: ' + (mins < 10 ? '0' : '') + mins + ':' + (secs < 10 ? '0' : '') + secs;
    }
  }

  function updateLivesDisplay() {
    var livesEl = document.getElementById('lives-remaining');
    if (livesEl) {
      livesEl.textContent = 'Lives: ' + lives;
    }
  }

  function setDemoPairs(sequence) {
    demoPairs = sequence;
    matchedPairs = [];
    matchHistory = [];
    redoStack = [];
    clearedTiles = {};
    gameCleared = false;
    inputDisabled = false;

    var banner = document.getElementById('winner-banner');
    if (banner) banner.classList.add('hidden');

    var allTiles = document.querySelectorAll('.tile');
    for (var i = 0; i < allTiles.length; i++) {
      allTiles[i].classList.remove('cleared', 'selected', 'matched');
    }

    for (var i = 0; i < sequence.length; i++) {
      var pair = sequence[i];
      for (var j = 0; j < pair.coords.length; j++) {
        var coord = pair.coords[j];
        var tile = document.querySelector('[data-cell="' + coord + '"]');
        if (tile) {
          tile.setAttribute('data-value', pair.value);
          tile.textContent = pair.value;
          tileValues[coord] = pair.value;
        }
      }
    }

    logEvent('demo_pairs', 'set_' + sequence.length + '_pairs');
  }

  function handleTileClick(cellId) {
    if (inputDisabled || replayInProgress || gameCleared) return;

    var tile = document.querySelector('[data-cell="' + cellId + '"]');
    if (!tile || clearedTiles[cellId]) return;

    if (selections.length === 1 && selections[0] === cellId) {
      tile.classList.remove('selected');
      selections = [];
      setStatusMessage('Select a different tile');
      return;
    }

    var found = false;
    for (var i = 0; i < selections.length; i++) {
      if (selections[i] === cellId) found = true;
    }
    if (found) return;

    tile.classList.add('selected');
    selections.push(cellId);

    if (selections.length === 2) {
      var cell1 = selections[0];
      var cell2 = selections[1];
      var tile1 = document.querySelector('[data-cell="' + cell1 + '"]');
      var tile2 = document.querySelector('[data-cell="' + cell2 + '"]');
      var value1 = tileValues[cell1];
      var value2 = tileValues[cell2];

      if (value1 === value2 && canConnect(cell1, cell2)) {
        performMatch(cell1, cell2, value1, false);
      } else {
        setTimeout(function() {
          tile1.classList.remove('selected');
          tile2.classList.remove('selected');
          if (value1 !== value2) {
            setStatusMessage('Values do not match');
          } else {
            setStatusMessage('No valid path');
          }
          comboCount = 0;
          updateCombo();
        }, 200);
      }

      selections = [];
    } else {
      setStatusMessage('Select second tile');
    }
  }

  function performMatch(cell1, cell2, value, skipAnimation) {
    var tile1 = document.querySelector('[data-cell="' + cell1 + '"]');
    var tile2 = document.querySelector('[data-cell="' + cell2 + '"]');

    if (!skipAnimation) {
      tile1.classList.add('matched');
      tile2.classList.add('matched');
    }

    var matchId = null;
    for (var i = 0; i < demoPairs.length; i++) {
      var pair = demoPairs[i];
      var hasCell1 = false, hasCell2 = false;
      for (var j = 0; j < pair.coords.length; j++) {
        if (pair.coords[j] === cell1) hasCell1 = true;
        if (pair.coords[j] === cell2) hasCell2 = true;
      }
      if (hasCell1 && hasCell2) {
        matchId = pair.id;
        break;
      }
    }

    if (!matchId) matchId = cell1 + '-' + cell2;

    matchedPairs.push(matchId);
    matchHistory.push({
      id: matchId,
      coords: [cell1, cell2],
      value: value
    });
    redoStack = [];
    comboCount++;
    updateCombo();
    logEvent('match', matchId);

    var finalize = function() {
      tile1.classList.remove('selected', 'matched');
      tile2.classList.remove('selected', 'matched');
      tile1.classList.add('cleared');
      tile2.classList.add('cleared');
      clearedTiles[cell1] = true;
      clearedTiles[cell2] = true;
      updateHUD();
      setStatusMessage('Match found!');
      checkWinCondition();
    };

    if (skipAnimation) {
      finalize();
    } else {
      setTimeout(finalize, 400);
    }
  }

  function checkWinCondition() {
    var result = checkCleared();
    if (result.cleared) {
      gameCleared = true;
      inputDisabled = true;
      if (timerInterval) clearInterval(timerInterval);
      var banner = document.getElementById('winner-banner');
      if (banner) {
        banner.textContent = 'Board cleared!';
        banner.classList.remove('hidden');
      }
      logEvent('win', 'board_cleared');
    }
  }

  function canConnect(cell1, cell2) {
    return true;
  }

  function updateHUD() {
    var pairsEl = document.getElementById('pairs-remaining');
    if (pairsEl) {
      var totalPairs = demoPairs.length > 0 ? demoPairs.length : 40;
      var remaining = totalPairs - matchedPairs.length;
      pairsEl.textContent = 'Pairs: ' + remaining;
    }
  }

  function updateCombo() {
    var comboEl = document.getElementById('combo-indicator');
    if (comboEl) {
      comboEl.textContent = 'Combo x' + comboCount;
    }
  }

  function setStatusMessage(msg) {
    var statusEl = document.getElementById('status-message');
    if (statusEl) statusEl.textContent = msg;
  }

  function triggerHint() {
    for (var i = 0; i < demoPairs.length; i++) {
      var pair = demoPairs[i];
      var found = false;
      for (var j = 0; j < matchedPairs.length; j++) {
        if (matchedPairs[j] === pair.id) found = true;
      }
      if (!found) {
        var tile1 = document.querySelector('[data-cell="' + pair.coords[0] + '"]');
        var tile2 = document.querySelector('[data-cell="' + pair.coords[1] + '"]');
        if (tile1 && tile2 && !clearedTiles[pair.coords[0]] && !clearedTiles[pair.coords[1]]) {
          tile1.classList.add('selected');
          tile2.classList.add('selected');
          setTimeout(function() {
            tile1.classList.remove('selected');
            tile2.classList.remove('selected');
          }, 1000);
          setStatusMessage('Hint: Try ' + pair.coords[0] + ' and ' + pair.coords[1]);
          logEvent('hint', pair.coords[0] + ',' + pair.coords[1]);
          return;
        }
      }
    }
    setStatusMessage('No hints available');
  }

  function applyShuffle() {
    shuffleCount++;
    setStatusMessage('Tiles shuffled');
    logEvent('shuffle', 'count_' + shuffleCount);
  }

  function undoMatch() {
    if (matchHistory.length === 0 || replayInProgress) return;

    var lastMatch = matchHistory.pop();
    redoStack.push(lastMatch);

    for (var i = 0; i < matchedPairs.length; i++) {
      if (matchedPairs[i] === lastMatch.id) {
        matchedPairs.splice(i, 1);
        break;
      }
    }

    for (var i = 0; i < lastMatch.coords.length; i++) {
      var coord = lastMatch.coords[i];
      delete clearedTiles[coord];
      var tile = document.querySelector('[data-cell="' + coord + '"]');
      if (tile) tile.classList.remove('cleared');
    }

    gameCleared = false;
    inputDisabled = false;
    var banner = document.getElementById('winner-banner');
    if (banner) banner.classList.add('hidden');

    updateHUD();
    setStatusMessage('Undo: restored ' + lastMatch.coords.join(' and '));
    logEvent('undo', lastMatch.id);
  }

  function redoMatch() {
    if (redoStack.length === 0 || replayInProgress) return;

    var match = redoStack.pop();
    performMatch(match.coords[0], match.coords[1], match.value, true);
    logEvent('redo', match.id);
    setStatusMessage('Redo: matched ' + match.coords.join(' and '));
  }

  function startReplay() {
    if (matchHistory.length === 0 || replayInProgress) return;

    replayInProgress = true;
    inputDisabled = true;
    logEvent('replay_start', 'matches_' + matchHistory.length);

    var historySnapshot = [];
    for (var i = 0; i < matchHistory.length; i++) {
      historySnapshot.push(matchHistory[i]);
    }

    for (var i = 0; i < matchHistory.length; i++) {
      var match = matchHistory[i];
      for (var j = 0; j < match.coords.length; j++) {
        var coord = match.coords[j];
        delete clearedTiles[coord];
        var tile = document.querySelector('[data-cell="' + coord + '"]');
        if (tile) tile.classList.remove('cleared');
      }
    }

    matchedPairs = [];
    matchHistory = [];
    updateHUD();

    var banner = document.getElementById('winner-banner');
    if (banner) banner.classList.add('hidden');
    gameCleared = false;

    var index = 0;

    function replayNext() {
      if (index >= historySnapshot.length) {
        replayInProgress = false;
        inputDisabled = gameCleared;
        logEvent('replay_end', 'completed');
        setStatusMessage('Replay complete');
        return;
      }

      var match = historySnapshot[index];
      var tile1 = document.querySelector('[data-cell="' + match.coords[0] + '"]');
      var tile2 = document.querySelector('[data-cell="' + match.coords[1] + '"]');

      if (tile1) tile1.classList.add('replay-highlight');
      if (tile2) tile2.classList.add('replay-highlight');

      setTimeout(function() {
        if (tile1) tile1.classList.remove('replay-highlight');
        if (tile2) tile2.classList.remove('replay-highlight');
        performMatch(match.coords[0], match.coords[1], match.value, true);
        index++;
        setTimeout(replayNext, 300);
      }, 300);
    }

    replayNext();
  }

  function checkCleared() {
    var totalPairs = demoPairs.length > 0 ? demoPairs.length : 40;
    var remaining = totalPairs - matchedPairs.length;
    return {
      cleared: remaining === 0,
      remaining: remaining
    };
  }

  function registerStrike() {
    lives--;
    updateLivesDisplay();
    logEvent('strike', 'lives_' + lives);
    if (lives <= 0) {
      inputDisabled = true;
      setStatusMessage('Game Over - No lives remaining');
      logEvent('game_over', 'no_lives');
    }
  }

  function exportLog() {
    var csv = 'timestamp,event,detail\n';
    for (var i = 0; i < sessionLog.length; i++) {
      var entry = sessionLog[i];
      csv += entry.timestamp + ',' + entry.event + ',' + entry.detail + '\n';
    }
    return csv;
  }

  function debugState() {
    var mp = [];
    for (var i = 0; i < matchedPairs.length; i++) mp.push(matchedPairs[i]);
    var sel = [];
    for (var i = 0; i < selections.length; i++) sel.push(selections[i]);
    return {
      selections: sel,
      matchedPairs: mp,
      shuffleCount: shuffleCount,
      timerSeconds: timerSeconds,
      lives: lives
    };
  }

  function describeLayout() {
    var result = {
      startButton: null,
      legend: null,
      hud: null,
      tiles: {}
    };

    var startBtn = document.getElementById('start-btn');
    if (startBtn) {
      var rect = startBtn.getBoundingClientRect();
      result.startButton = {
        x: Math.round(rect.left + rect.width / 2),
        y: Math.round(rect.top + rect.height / 2)
      };
    }

    var legend = document.getElementById('legend');
    if (legend) {
      var rect = legend.getBoundingClientRect();
      result.legend = {
        top: Math.round(rect.top),
        left: Math.round(rect.left),
        width: Math.round(rect.width),
        height: Math.round(rect.height)
      };
    }

    var hud = document.getElementById('hud');
    if (hud) {
      var rect = hud.getBoundingClientRect();
      result.hud = {
        top: Math.round(rect.top),
        left: Math.round(rect.left),
        width: Math.round(rect.width),
        height: Math.round(rect.height)
      };
    }

    var tiles = document.querySelectorAll('.tile');
    for (var i = 0; i < tiles.length; i++) {
      var tile = tiles[i];
      var cellId = tile.getAttribute('data-cell');
      if (cellId) {
        var rect = tile.getBoundingClientRect();
        result.tiles[cellId] = {
          x: Math.round(rect.left + rect.width / 2),
          y: Math.round(rect.top + rect.height / 2)
        };
      }
    }

    return result;
  }

  function setupControls() {
    var hintBtn = document.getElementById('hint-button');
    var shuffleBtn = document.getElementById('shuffle-button');
    var undoBtn = document.querySelector('button[data-action="undo"]');
    var redoBtn = document.querySelector('button[data-action="redo"]');
    var replayBtn = document.querySelector('button[data-action="replay"]');

    if (hintBtn) hintBtn.addEventListener('click', triggerHint);
    if (shuffleBtn) shuffleBtn.addEventListener('click', applyShuffle);
    if (undoBtn) undoBtn.addEventListener('click', undoMatch);
    if (redoBtn) redoBtn.addEventListener('click', redoMatch);
    if (replayBtn) replayBtn.addEventListener('click', startReplay);
  }

  window.app = {
    initializeGrid: initializeGrid,
    describeLayout: describeLayout,
    debugState: debugState,
    setDemoPairs: setDemoPairs,
    triggerHint: triggerHint,
    applyShuffle: applyShuffle,
    undoMatch: undoMatch,
    redoMatch: redoMatch,
    startReplay: startReplay,
    checkCleared: checkCleared,
    registerStrike: registerStrike,
    exportLog: exportLog
