    <head>
      <meta charset="UTF-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
      <title>Lianliankan - Tile Matching Puzzle</title>
      <link rel="stylesheet" href="styles.css">
    </head>
    <body>
      <button id="start-btn">Start Puzzle</button>

      <div id="winner-banner" class="hidden">Board cleared!</div>

      <div id="game-container">
        <div id="hud">
          <span id="timer">Time: 01:30</span>
          <span id="lives-remaining">Lives: 3</span>
          <span id="pairs-remaining">Pairs: 40</span>
          <button id="hint-button">Hint</button>
          <button id="shuffle-button">Shuffle</button>
          <span id="combo-indicator">Combo x0</span>
        </div>

        <div id="puzzle-wrapper">
          <div id="column-labels">
            <span>A</span><span>B</span><span>C</span><span>D</span><span>E</span>
            <span>F</span><span>G</span><span>H</span><span>I</span><span>J</span>
          </div>
          <div id="board-area">
            <div id="row-labels">
              <span>1</span><span>2</span><span>3</span><span>4</span>
              <span>5</span><span>6</span><span>7</span><span>8</span>
            </div>
            <div id="tile-grid"></div>
          </div>
        </div>

        <div id="status-bar">
          <span id="status-message">Click tiles to match pairs</span>
        </div>

        <div id="controls">
          <button data-action="undo">Undo</button>
          <button data-action="redo">Redo</button>
          <button data-action="replay">Replay</button>
        </div>

        <div id="legend">
          <h3>Tile Colors</h3>
          <div class="legend-items">
            <div class="legend-item"><span class="color-box" style="background:#FF6B6B;"></span>Red</div>
            <div class="legend-item"><span class="color-box" style="background:#4ECDC4;"></span>Teal</div>
            <div class="legend-item"><span class="color-box" style="background:#45B7D1;"></span>Blue</div>
            <div class="legend-item"><span class="color-box" style="background:#96CEB4;"></span>Green</div>
            <div class="legend-item"><span class="color-box" style="background:#FFEAA7;"></span>Yellow</div>
            <div class="legend-item"><span class="color-box" style="background:#DDA0DD;"></span>Purple</div>
            <div class="legend-item"><span class="color-box" style="background:#F39C12;"></span>Orange</div>
            <div class="legend-item"><span class="color-box" style="background:#E056FD;"></span>Pink</div>
          </div>
        </div>
      </div>

      <script src="app.js"></script>
    </body>
    </html>

