[[workspace]] styles.css
    * {
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }

    body {
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
      background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
      min-height: 100vh;
      color: #fff;
      position: relative;
    }

    #start-btn {
      position: absolute;
      top: 110px;
      left: 120px;
      transform: translate(-50%, -50%);
      padding: 12px 24px;
      font-size: 16px;
      font-weight: bold;
      background: linear-gradient(135deg, #4ECDC4 0%, #44A08D 100%);
      color: #fff;
      border: none;
      border-radius: 20px;
      cursor: pointer;
      box-shadow: 0 4px 15px rgba(78, 205, 196, 0.4);
      z-index: 100;
    }

    #start-btn:hover {
      transform: translate(-50%, -50%) scale(1.05);
    }

    #winner-banner {
      position: fixed;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      background: linear-gradient(135deg, #4ECDC4 0%, #44A08D 100%);
      color: #fff;
      padding: 30px 60px;
      font-size: 32px;
      font-weight: bold;
      border-radius: 16px;
      box-shadow: 0 8px 32px rgba(78, 205, 196, 0.6);
      z-index: 1000;
      text-align: center;
    }

    #winner-banner.hidden {
      display: none;
    }

    #game-container {
      display: flex;
      flex-direction: column;
      align-items: center;
      padding-top: 48px;
    }

    #hud {
      display: flex;
      gap: 20px;
      font-size: 16px;
      font-weight: 600;
      background: rgba(255, 255, 255, 0.1);
      padding: 10px 20px;
      border-radius: 8px;
      margin-bottom: 10px;
      align-items: center;
    }

    #timer {
      color: #4ECDC4;
    }

    #lives-remaining {
      color: #FF6B6B;
    }

    #pairs-remaining {
      color: #96CEB4;
    }

    #hint-button, #shuffle-button {
      padding: 6px 14px;
      font-size: 14px;
      font-weight: bold;
      background: rgba(255, 255, 255, 0.2);
      color: #fff;
      border: 1px solid rgba(255, 255, 255, 0.3);
      border-radius: 6px;
      cursor: pointer;
      transition: all 0.2s ease;
    }

    #hint-button:hover, #shuffle-button:hover {
      background: rgba(255, 255, 255, 0.3);
    }

    #combo-indicator {
      color: #FFEAA7;
      font-weight: bold;
    }

    #puzzle-wrapper {
      display: flex;
      flex-direction: column;
      align-items: center;
    }

    #column-labels {
      display: flex;
      width: 640px;
      margin-left: 24px;
      margin-bottom: 4px;
    }

    #column-labels span {
      width: 64px;
      text-align: center;
      font-weight: bold;
      font-size: 14px;
      color: #aaa;
    }

    #board-area {
      display: flex;
    }

    #row-labels {
      display: flex;
      flex-direction: column;
      margin-right: 4px;
    }

    #row-labels span {
      height: 64px;
      width: 20px;
      display: flex;
      align-items: center;
      justify-content: center;
      font-weight: bold;
      font-size: 14px;
      color: #aaa;
    }

    #tile-grid {
      width: 640px;
      height: 512px;
      display: grid;
      grid-template-columns: repeat(10, 64px);
      grid-template-rows: repeat(8, 64px);
      background: rgba(0, 0, 0, 0.3);
      border-radius: 8px;
      border: 2px solid rgba(255, 255, 255, 0.1);
    }

    .tile {
      width: 64px;
      height: 64px;
      display: flex;
      align-items: center;
      justify-content: center;
      background: rgba(255, 255, 255, 0.08);
      border: 1px solid rgba(255, 255, 255, 0.15);
      border-radius: 6px;
      cursor: pointer;
      transition: all 0.2s ease;
      font-size: 28px;
      color: rgba(255, 255, 255, 0.3);
    }

    .tile:hover:not(.cleared) {
      background: rgba(255, 255, 255, 0.15);
    }

    .tile.selected {
      background: rgba(78, 205, 196, 0.3);
      border-color: #4ECDC4;
      box-shadow: 0 0 10px rgba(78, 205, 196, 0.5);
    }

    .tile.matched {
      animation: pulse-match 0.4s ease-out;
    }

    .tile.cleared {
      opacity: 0;
      pointer-events: none;
      background: transparent;
      border-color: transparent;
    }

    .tile.replay-highlight {
      background: rgba(255, 215, 0, 0.4);
      border-color: #FFD700;
      box-shadow: 0 0 15px rgba(255, 215, 0, 0.6);
    }

    @keyframes pulse-match {
      0% {
        transform: scale(1);
        background: rgba(78, 205, 196, 0.5);
      }
      50% {
        transform: scale(1.1);
        background: rgba(78, 205, 196, 0.8);
      }
      100% {
        transform: scale(1);
        background: rgba(78, 205, 196, 0.3);
      }
    }

    #status-bar {
      margin-top: 15px;
      padding: 10px 20px;
      background: rgba(255, 255, 255, 0.05);
      border-radius: 6px;
      min-width: 300px;
      text-align: center;
    }

    #status-message {
      color: #aaa;
      font-size: 14px;
    }

    #controls {
      margin-top: 15px;
      display: flex;
      gap: 15px;
    }

    #controls button {
      padding: 8px 20px;
      font-size: 14px;
      font-weight: bold;
      background: rgba(255, 255, 255, 0.15);
      color: #fff;
      border: 1px solid rgba(255, 255, 255, 0.25);
      border-radius: 6px;
      cursor: pointer;
      transition: all 0.2s ease;
    }

    #controls button:hover {
      background: rgba(255, 255, 255, 0.25);
    }

    #controls button:disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    #legend {
      margin-top: 70px;
      background: rgba(255, 255, 255, 0.05);
      padding: 15px 25px;
      border-radius: 8px;
      text-align: center;
    }

    #legend h3 {
      margin-bottom: 10px;
      font-size: 14px;
      color: #ccc;
    }

    .legend-items {
      display: flex;
      flex-wrap: wrap;
      gap: 15px;
      justify-content: center;
    }

    .legend-item {
      display: flex;
      align-items: center;
      gap: 5px;
      font-size: 12px;
      color: #aaa;
    }

    .color-box {
      width: 14px;
      height: 14px;
      border-radius: 3px;
    }

