        box-shadow: 0 0 25px rgba(0, 255, 136, 0.7), 0 8px 20px rgba(0, 0, 0, 0.4);
    }

    .avatar::before {
        content: '';
        position: absolute;
        top: 8px;
        left: 50%;
        transform: translateX(-50%);
        width: 24px;
        height: 12px;
        background: rgba(255, 255, 255, 0.9);
        border-radius: 50%;
        box-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
    }

    .avatar::after {
        content: '';
        position: absolute;
        bottom: -5px;
        left: 50%;
        transform: translateX(-50%);
        width: 30px;
        height: 8px;
        background: rgba(0, 255, 136, 0.4);
        border-radius: 50%;
        filter: blur(3px);
    }

