            nextPlatform: document.getElementById('next-platform'),
            chargeMeter: document.getElementById('charge-meter'),
            chargeFill: document.getElementById('charge-fill'),
            statusPanel: document.getElementById('status-panel'),
            lastLanding: document.getElementById('last-landing')
        };
    }

    function setStatus(text) {
        var els = getElements();
        if (els.statusPanel) {
            els.statusPanel.textContent = text;
        }
        if (text === 'Ready') phase = 'ready';
        else if (text === 'Charging') phase = 'charging';
        else if (text === 'Jump landed' || text === 'Jump failed') phase = 'landed';
    }

    function setCharge(percent) {
        var els = getElements();
        if (els.chargeFill) {
            els.chargeFill.style.width = Math.min(100, Math.max(0, percent)) + '%';
        }
    }

    function positionLandingHalo(platformEl) {
        var els = getElements();
        if (!els.lastLanding || !platformEl) return;

        var platRect = platformEl.getBoundingClientRect();
        var stageRect = els.stage.getBoundingClientRect();

        var centerX = (platformEl.offsetLeft + platformEl.offsetWidth / 2);
        var centerY = (platformEl.offsetTop + platformEl.offsetHeight / 2);

        els.lastLanding.style.left = centerX + 'px';
        els.lastLanding.style.top = centerY + 'px';
        els.lastLanding.classList.add('active');
    }

    function swapPlatforms() {
        var els = getElements();

        var tempPos = { left: currentPlatformPos.left, top: currentPlatformPos.top };
        currentPlatformPos.left = nextPlatformPos.left;
        currentPlatformPos.top = nextPlatformPos.top;

        nextPlatformPos.left = 480 + Math.random() * 100;
        nextPlatformPos.top = 180 + Math.random() * 60;

        els.currentPlatform.style.left = currentPlatformPos.left + 'px';
        els.currentPlatform.style.top = currentPlatformPos.top + 'px';
        els.nextPlatform.style.left = nextPlatformPos.left + 'px';
        els.nextPlatform.style.top = nextPlatformPos.top + 'px';

        avatarPos.left = currentPlatformPos.left + 27;
        avatarPos.top = currentPlatformPos.top - 44;
        els.avatar.style.left = avatarPos.left + 'px';
        els.avatar.style.top = avatarPos.top + 'px';
    }

    function executeJump(jumpData, callback) {
        var els = getElements();

        setStatus('Charging');

        var chargePercent = 0;
        var chargeStep = 100 / (jumpData.holdMs / 50);

        var chargeAnim = setInterval(function() {
            chargePercent += chargeStep;
            setCharge(chargePercent);
            if (chargePercent >= 100) {
                clearInterval(chargeAnim);
            }
        }, 50);

        setTimeout(function() {
            clearInterval(chargeAnim);
            setCharge(100);

            els.avatar.classList.add('jumping');

            var targetLeft, targetTop;
            if (jumpData.target === 'near') {
                targetLeft = currentPlatformPos.left + 150;
                targetTop = currentPlatformPos.top - 44;
            } else {
                targetLeft = nextPlatformPos.left + 27;
                targetTop = nextPlatformPos.top - 44;
            }

            els.avatar.style.left = targetLeft + 'px';
            els.avatar.style.top = targetTop + 'px';

            setTimeout(function() {
                els.avatar.classList.remove('jumping');
                setCharge(0);

                if (jumpData.result === 'landed') {
                    setStatus('Jump landed');
                    swapPlatforms();
                    positionLandingHalo(els.currentPlatform);
                } else {
                    setStatus('Jump failed');
                }

                setTimeout(function() {
                    setStatus('Ready');
                    if (callback) callback();
                }, 300);
            }, 400);
        }, jumpData.holdMs);
    }

    function startCharging() {
        if (isCharging) return;
        isCharging = true;
        chargeStartTime = Date.now();
        setStatus('Charging');

        chargeInterval = setInterval(function() {
            var elapsed = Date.now() - chargeStartTime;
            var percent = Math.min(100, (elapsed / 1000) * 100);
            setCharge(percent);
        }, 50);
    }

    function releaseCharge() {
        if (!isCharging) return;
        isCharging = false;

        if (chargeInterval) {
            clearInterval(chargeInterval);
            chargeInterval = null;
        }

        var holdMs = Date.now() - chargeStartTime;
        var target = holdMs > 300 ? 'far' : 'near';
        var result = holdMs > 200 ? 'landed' : 'missed';

        executeJump({ id: 'manual-' + Date.now(), holdMs: holdMs, target: target, result: result }, null);
    }

    function initializeStage() {
        if (initialized) return;
