        var els = getElements();

        setStatus('Ready');
        setCharge(0);

        els.stage.addEventListener('mousedown', startCharging);
        els.stage.addEventListener('mouseup', releaseCharge);
        els.stage.addEventListener('mouseleave', function() {
            if (isCharging) releaseCharge();
