                if (isCharging) releaseCharge();
            });

            document.addEventListener('keydown', function(e) {
                if (e.code === 'Space' && !e.repeat) {
                    e.preventDefault();
                    startCharging();
                }
            });

            document.addEventListener('keyup', function(e) {
                if (e.code === 'Space') {
                    e.preventDefault();
                    releaseCharge();
                }
            });
        }

        function describeStage() {
            var els = getElements();

            var btnRect = els.launchBtn.getBoundingClientRect();
            var avatarRect = els.avatar.getBoundingClientRect();
            var currentRect = els.currentPlatform.getBoundingClientRect();
            var nextRect = els.nextPlatform.getBoundingClientRect();

            return {
                launchButton: {
                    x: Math.round(btnRect.left + btnRect.width / 2),
                    y: Math.round(btnRect.top + btnRect.height / 2)
                },
                avatar: {
                    x: Math.round(avatarRect.left + avatarRect.width / 2),
                    y: Math.round(avatarRect.top + avatarRect.height / 2)
                },
                platforms: {
                    current: {
                        x: Math.round(currentRect.left + currentRect.width / 2),
                        y: Math.round(currentRect.top + currentRect.height / 2)
                    },
                    next: {
                        x: Math.round(nextRect.left + nextRect.width / 2),
                        y: Math.round(nextRect.top + nextRect.height / 2)
                    }
                }
            };
        }

        function debugRun() {
            return {
                phase: phase,
                jumps: jumps.slice()
            };
        }

        function setDemoJumps(sequence) {
            if (!sequence || !Array.isArray(sequence)) return;

            var index = 0;

            function playNext() {
                if (index >= sequence.length) return;

                var jumpData = sequence[index];

                if (seenJumpIds.has(jumpData.id)) {
                    index++;
                    playNext();
                    return;
                }

                seenJumpIds.add(jumpData.id);

                executeJump(jumpData, function() {
                    jumps.push({
                        id: jumpData.id,
                        holdMs: jumpData.holdMs,
                        target: jumpData.target,
                        result: jumpData.result
                    });

                    index++;
                    setTimeout(playNext, 200);
                });
            }

            playNext();
        }

        window.app = {
            initializeStage: initializeStage,
            describeStage: describeStage,
            debugRun: debugRun,
            setDemoJumps: setDemoJumps
        };

        window.app.initializeStage();
    })();

