      "content": "(function() {
    var initialized = false;
    var phase = 'ready';
    var jumps = [];
    var seenJumpIds = new Set();
    var chargeStartTime = null;
    var chargeInterval = null;
    var isCharging = false;
    var isReplaying = false;
    var replayIndex = 0;
    var comboCount = 0;
    var runLog = [];
    var jumpHistory = [];
    var historyIndex = -1;

    var currentPlatformPos = { left: 120, top: 225 };
    var nextPlatformPos = { left: 500, top: 200 };
    var avatarPos = { left: 147, top: 181 };

    function getElements() {
        return {
            stage: document.getElementById('jump-stage'),
            launchBtn: document.getElementById('launch-btn'),
            avatar: document.querySelector('.avatar'),
            currentPlatform: document.getElementById('current-platform'),
            nextPlatform: document.getElementById('next-platform'),
            chargeMeter: document.getElementById('charge-meter'),
            chargeFill: document.getElementById('charge-fill'),
            statusPanel: document.getElementById('status-panel'),
            lastLanding: document.getElementById('last-landing'),
            comboBanner: document.getElementById('combo-banner'),
            controls: document.getElementById('controls')
        };
    }

    function setStatus(text) {
        var els = getElements();
        if (els.statusPanel) {
            els.statusPanel.textContent = text;
        }
        if (text === 'Ready') phase = 'ready';
        else if (text === 'Charging') phase = 'charging';
        else if (text === 'Jump landed' || text === 'Jump failed') phase = 'landed';
    }

    function setCharge(percent) {
        var els = getElements();
        if (els.chargeFill) {
            els.chargeFill.style.width = Math.min(100, Math.max(0, percent)) + '%';
        }
    }

    function showComboBanner(text) {
        var els = getElements();
        if (els.comboBanner) {
            els.comboBanner.textContent = text;
            els.comboBanner.classList.add('visible');
        }
    }

    function hideComboBanner() {
        var els = getElements();
        if (els.comboBanner) {
            els.comboBanner.classList.remove('visible');
        }
    }

    function lockStage() {
        var els = getElements();
        if (els.stage) {
            els.stage.classList.add('locked');
        }
    }

    function unlockStage() {
        var els = getElements();
        if (els.stage) {
            els.stage.classList.remove('locked');
        }
    }

    function positionLandingHalo(platformEl) {
        var els = getElements();
        if (!els.lastLanding || !platformEl) return;

        var centerX = (platformEl.offsetLeft + platformEl.offsetWidth / 2);
        var centerY = (platformEl.offsetTop + platformEl.offsetHeight / 2);

        els.lastLanding.style.left = centerX + 'px';
        els.lastLanding.style.top = centerY + 'px';
        els.lastLanding.classList.add('active');
    }

    function calculateDistance(target) {
        if (target === 'near') {
            return Math.round(Math.sqrt(Math.pow(150, 2) + Math.pow(20, 2)));
        } else {
            return Math.round(Math.sqrt(Math.pow(nextPlatformPos.left - currentPlatformPos.left, 2) + Math.pow(nextPlatformPos.top - currentPlatformPos.top, 2)));
        }
    }

    function logJump(jumpData, distance) {
        var timestamp = new Date().toISOString();
        var logEntry = timestamp + ',' + jumpData.holdMs + ',' + jumpData.target + ',' + jumpData.result + ',' + distance;
        runLog.push(logEntry);
    }

    function logReplayMarker(action) {
        var timestamp = new Date().toISOString();
        var logEntry = timestamp + ',REPLAY,' + action + ',,'; 
        runLog.push(logEntry);
    }

    function saveState() {
        var state = {
            avatarPos: { left: avatarPos.left, top: avatarPos.top },
            currentPlatformPos: { left: currentPlatformPos.left, top: currentPlatformPos.top },
            nextPlatformPos: { left: nextPlatformPos.left, top: nextPlatformPos.top },
            comboCount: comboCount
        };
        jumpHistory.push(JSON.parse(JSON.stringify(state)));
        historyIndex = jumpHistory.length - 1;
    }

    function swapPlatforms() {
        var els = getElements();

        currentPlatformPos.left = nextPlatformPos.left;
        currentPlatformPos.top = nextPlatformPos.top;

        nextPlatformPos.left = 480 + Math.random() * 100;
        nextPlatformPos.top = 180 + Math.random() * 60;

        els.currentPlatform.style.left = currentPlatformPos.left + 'px';
        els.currentPlatform.style.top = currentPlatformPos.top + 'px';
        els.nextPlatform.style.left = nextPlatformPos.left + 'px';
        els.nextPlatform.style.top = nextPlatformPos.top + 'px';

        avatarPos.left = currentPlatformPos.left + 27;
        avatarPos.top = currentPlatformPos.top - 44;
        els.avatar.style.left = avatarPos.left + 'px';
        els.avatar.style.top = avatarPos.top + 'px';
