                halo.style.left = cx + 'px';
                halo.style.top = cy + 'px';
                halo.classList.add('active');
            }
        }

        function calcDistance(target) {
            if (target === 'near') return 150;
            var dx = positions.next.left - positions.current.left;
            var dy = positions.next.top - positions.current.top;
            return Math.round(Math.sqrt(dx*dx + dy*dy));
        }

        function appendLog(line) {
            runLogLines.push(line);
        }

        function saveSnapshot() {
            jumpHistory.push({
                avatar: { left: positions.avatar.left, top: positions.avatar.top },
                current: { left: positions.current.left, top: positions.current.top },
                next: { left: positions.next.left, top: positions.next.top },
                combo: comboCount,
                score: score
            });
            historyIndex = jumpHistory.length - 1;
        }

        function applySnapshot(snap) {
            positions.avatar.left = snap.avatar.left;
            positions.avatar.top = snap.avatar.top;
            positions.current.left = snap.current.left;
            positions.current.top = snap.current.top;
            positions.next.left = snap.next.left;
            positions.next.top = snap.next.top;
            comboCount = snap.combo;
            score = snap.score;
            renderPositions();
            updateHalo();
        }

        function renderPositions() {
            var avatar = $('.avatar');
            var curPlat = $('#current-platform');
            var nextPlat = $('#next-platform');
            if (avatar) {
                avatar.style.left = positions.avatar.left + 'px';
                avatar.style.top = positions.avatar.top + 'px';
            }
            if (curPlat) {
                curPlat.style.left = positions.current.left + 'px';
                curPlat.style.top = positions.current.top + 'px';
            }
            if (nextPlat) {
                nextPlat.style.left = positions.next.left + 'px';
                nextPlat.style.top = positions.next.top + 'px';
            }
        }

        function swapPlatforms() {
            positions.current.left = positions.next.left;
            positions.current.top = positions.next.top;
            positions.next.left = 450 + Math.floor(Math.random() * 120);
            positions.next.top = 170 + Math.floor(Math.random() * 80);
            positions.avatar.left = positions.current.left + 27;
            positions.avatar.top = positions.current.top - 44;
            renderPositions();
        }

        function doJump(data, cb) {
            saveSnapshot();
            setStatus('Charging');
            var pct = 0;
            var step = 100 / (data.holdMs / 30);
            var iv = setInterval(function() {
                pct += step;
                setCharge(Math.min(pct, 100));
                if (pct >= 100) clearInterval(iv);
            }, 30);

            setTimeout(function() {
                clearInterval(iv);
                setCharge(100);
                var tgtLeft, tgtTop;
                if (data.target === 'far') {
                    tgtLeft = positions.next.left + 27;
                    tgtTop = positions.next.top - 44;
                } else {
                    tgtLeft = positions.current.left + 140;
                    tgtTop = positions.current.top - 44;
                }
                positions.avatar.left = tgtLeft;
                positions.avatar.top = tgtTop;
                renderPositions();

                setTimeout(function() {
                    setCharge(0);
                    var dist = calcDistance(data.target);
                    var ts = new Date().toISOString();
                    appendLog(ts + ',' + data.holdMs + ',' + data.target + ',' + data.result + ',' + dist);

                    if (data.result === 'landed') {
                        comboCount++;
                        score += 100 * comboCount;
                        setStatus('Jump landed');
                        swapPlatforms();
                        updateHalo();
                        if (comboCount >= 5) {
                            showCombo('Perfect chain!');
                            lockStage();
                        } else if (comboCount >= 3) {
                            showCombo('Combo x' + comboCount + '!');
                        }
                    } else {
                        comboCount = 0;
                        setStatus('Jump failed');
                        hideCombo();
                    }

                    jumps.push({ id: data.id, holdMs: data.holdMs, target: data.target, result: data.result });

                    setTimeout(function() {
                        if (comboCount < 5) setStatus('Ready');
                        if (cb) cb();
                    }, 150);
                }, 250);
            }, data.holdMs);
        }

        function rewind() {
            if (historyIndex > 0) {
                historyIndex--;
                applySnapshot(jumpHistory[historyIndex]);
                appendLog(new Date().toISOString() + ',REPLAY,rewind,,');
            }
        }

        function fastForward() {
            if (historyIndex < jumpHistory.length - 1) {
                historyIndex++;
                applySnapshot(jumpHistory[historyIndex]);
                appendLog(new Date().toISOString() + ',REPLAY,fast-forward,,');
            }
        }

        function startReplay() {
            if (jumpHistory.length < 2) return;
            isReplaying = true;
            lockStage();
            appendLog(new Date().toISOString() + ',REPLAY,start,,');
            var idx = 0;
            function step() {
                if (idx >= jumpHistory.length) {
                    isReplaying = false;
                    if (comboCount < 5) unlockStage();
                    appendLog(new Date().toISOString() + ',REPLAY,end,,');
                    return;
                }
                applySnapshot(jumpHistory[idx]);
                idx++;
                setTimeout(step, 300);
            }
            step();
        }

        function checkLandingStatus() {
            if (comboCount >= 5) return 'combo';
            if (comboCount > 0) return 'landed';
            return 'ready';
        }

        function exportRunLog() {
            return runLogLines.join('\n');
        }

        function debugRun() {
            return { phase: phase, jumps: jumps.slice() };
        }

        function setDemoJumps(seq) {
            if (!seq || !Array.isArray(seq)) return;
            isReplaying = true;
            lockStage();
            var i = 0;
            function next() {
                if (i >= seq.length) {
                    isReplaying = false;
                    if (comboCount < 5) unlockStage();
                    return;
                }
                var j = seq[i];
                if (seenJumpIds[j.id]) { i++; next(); return; }
                seenJumpIds[j.id] = true;
                doJump(j, function() { i++; setTimeout(next, 100); });
            }
            next();
        }

        function describeStage() {
            var btn = $('#launch-btn');
            var av = $('.avatar');
            var cp = $('#current-platform');
            var np = $('#next-platform');
            var br = btn.getBoundingClientRect();
            var ar = av.getBoundingClientRect();
            var cr = cp.getBoundingClientRect();
            var nr = np.getBoundingClientRect();
            return {
                launchButton: { x: Math.round(br.left + br.width/2), y: Math.round(br.top + br.height/2) },
                avatar: { x: Math.round(ar.left + ar.width/2), y: Math.round(ar.top + ar.height/2) },
                platforms: {
                    current: { x: Math.round(cr.left + cr.width/2), y: Math.round(cr.top + cr.height/2) },
                    next: { x: Math.round(nr.left + nr.width/2), y: Math.round(nr.top + nr.height/2) }
                }
            };
        }

        function initializeStage() {
            if (initialized) return;
            initialized = true;
            saveSnapshot();
            setStatus('Ready');
            setCharge(0);

            var stage = $('#jump-stage');
            var charging = false, startT = 0, civ = null;

            stage.addEventListener('mousedown', function() {
                if (isReplaying) return;
                charging = true;
                startT = Date.now();
                setStatus('Charging');
                civ = setInterval(function() {
                    var p = Math.min(100, (Date.now() - startT) / 10);
                    setCharge(p);
                }, 30);
            });

            stage.addEventListener('mouseup', function() {
                if (!charging) return;
                charging = false;
                clearInterval(civ);
                var hold = Date.now() - startT;
                var tgt = hold > 300 ? 'far' : 'near';
                var res = hold > 150 ? 'landed' : 'missed';
                doJump({ id: 'manual-' + Date.now(), holdMs: hold, target: tgt, result: res }, null);
            });

            $('button[data-action="rewind"]').addEventListener('click', rewind);
            $('button[data-action="fast-forward"]').addEventListener('click', fastForward);
            $('button[data-action="replay"]').addEventListener('click', startReplay);
        }

        window.app = {
            initializeStage: initializeStage,
            describeStage: describeStage,
            debugRun: debugRun,
            setDemoJumps: setDemoJumps,
            rewind: rewind,
            fastForward: fastForward,
            startReplay: startReplay,
            checkLandingStatus: checkLandingStatus,
            exportRunLog: exportRunLog
        };

        initializeStage();
    })();

