}

@keyframes pulse-ring {
    0% { transform: translate(-50%, -50%) scale(1); opacity: 1; box-shadow: 0 0 10px #ff00ff; }
    50% { transform: translate(-50%, -50%) scale(1.3); opacity: 0.7; box-shadow: 0 0 20px #ff00ff, 0 0 30px #00ffff; }
